def solver(input_grid):
    import numpy as np
    from collections import Counter

    grid = np.array(input_grid)
    rows, cols = grid.shape

    # Find background color (most common)
    bg_color = Counter(grid.flatten()).most_common(1)[0][0]

    # Find connected components using BFS
    visited = np.zeros((rows, cols), dtype=bool)
    components = []

    for r in range(rows):
        for c in range(cols):
            if grid[r, c] != bg_color and not visited[r, c]:
                queue = [(r, c)]
                cells = []
                while queue:
                    cr, cc = queue.pop(0)
                    if not (0 <= cr < rows and 0 <= cc < cols) or visited[cr, cc] or grid[cr, cc] == bg_color:
                        continue
                    visited[cr, cc] = True
                    cells.append((cr, cc))
                    for dr, dc in [(-1,0),(1,0),(0,-1),(0,1)]:
                        queue.append((cr+dr, cc+dc))

                if cells:
                    min_r, max_r = min(c[0] for c in cells), max(c[0] for c in cells)
                    min_c, max_c = min(c[1] for c in cells), max(c[1] for c in cells)
                    data = grid[min_r:max_r+1, min_c:max_c+1].copy()
                    if data.shape[0] > 3 or data.shape[1] > 3:
                        components.append(data)

    if len(components) <= 1:
        return components[0].tolist() if components else input_grid

    def get_core(arr):
        return arr[1:4] if arr.shape[0] == 5 else arr

    def try_merge(a, b, bg):
