def solver(input_grid):
    import numpy as np
    from collections import deque

    grid = np.array(input_grid, copy=False)
    h, w = grid.shape
    nbrs = ((1, 0), (-1, 0), (0, 1), (0, -1))

    # ---- find "noise" color (scattered, tiny components) ----
    colors = [int(c) for c in np.unique(grid) if c != 0]
    if not colors:
        return grid.copy()

    def color_component_stats(color):
        mask = (grid == color)
        total = int(mask.sum())
        if total == 0:
            return 0, 0, 0

        visited = np.zeros((h, w), dtype=bool)
        max_size = 0
        n_comp = 0

        for r in range(h):
            for c in range(w):
                if mask[r, c] and not visited[r, c]:
                    n_comp += 1
                    q = deque([(r, c)])
                    visited[r, c] = True
                    size = 0
                    while q:
                        x, y = q.popleft()
                        size += 1
                        for dx, dy in nbrs:
                            nx, ny = x + dx, y + dy
                            if 0 <= nx < h and 0 <= ny < w and mask[nx, ny] and not visited[nx, ny]:
                                visited[nx, ny] = True
                                q.append((nx, ny))
                    if size > max_size:
                        max_size = size
        return max_size, n_comp, total

    noise_candidates = []
    for c in colors:
        max_size, n_comp, total = color_component_stats(c)
        # Prefer smallest largest component; if tied, prefer more scattered and more pixels.
        noise_candidates.append((max_size, -n_comp, -total, c))
    noise_color = min(noise_candidates)[3]

    frame_colors = [c for c in colors if c != noise_color]

    # ---- find enclosed 0-regions ("panes") and assign them to a frame color ----
    pane_count = {c: 0 for c in frame_colors}
    visited0 = np.zeros((h, w), dtype=bool)

    for sr in range(h):
        for sc in range(w):
            if grid[sr, sc] != 0 or visited0[sr, sc]:
                continue

            q = deque([(sr, sc)])
            visited0[sr, sc] = True
            cells = [(sr, sc)]
            touches_border = (sr == 0 or sr == h - 1 or sc == 0 or sc == w - 1)

            while q:
                r, c = q.popleft()
                for dr, dc in nbrs:
                    nr, nc = r + dr, c + dc
                    if 0 <= nr < h and 0 <= nc < w and not visited0[nr, nc] and grid[nr, nc] == 0:
                        visited0[nr, nc] = True
                        q.append((nr, nc))
                        cells.append((nr, nc))
                        if nr == 0 or nr == h - 1 or nc == 0 or nc == w - 1:
                            touches_border = True

            if touches_border:
                continue  # outside background, not a pane

            # Boundary color histogram (by adjacency length)
            hist = np.zeros(10, dtype=int)
            for r, c in cells:
                for dr, dc in nbrs:
                    nr, nc = r + dr, c + dc
                    if 0 <= nr < h and 0 <= nc < w:
                        col = int(grid[nr, nc])
                        if col != 0:
                            hist[col] += 1

            hist[0] = 0
            hist_wo_noise = hist.copy()
            hist_wo_noise[noise_color] = 0

            if hist_wo_noise.sum() > 0:
                owner = int(np.argmax(hist_wo_noise))
            else:
                owner = int(np.argmax(hist))

            if owner in pane_count:
                pane_count[owner] += 1

    # Keep only colors that actually own panes (should be all frame colors in this task)
    frame_colors = [c for c in frame_colors if pane_count.get(c, 0) > 0]
    if not frame_colors:
        return np.array([[noise_color]], dtype=int)

    max_panes = max(pane_count[c] for c in frame_colors)
    out_w = max(4, max_panes)  # examples use 4

    # Sort by pane count ascending; tie-breaker by left-to-right position
    items = []
    for c in frame_colors:
        ys, xs = np.where(grid == c)
        y_min = int(ys.min()) if ys.size else 0
        x_min = int(xs.min()) if xs.size else 0
        items.append((pane_count[c], x_min, y_min, c))
    items.sort()

    out = np.full((len(items), out_w), noise_color, dtype=int)
    for i, (cnt, _, __, col) in enumerate(items):
        out[i, :cnt] = col

    return out
