                                    result[r][hint_c] = fill_color
                    result[marker_pos[0]][marker_pos[1]] = marker
                    return result

        # XOR approach for patterns without markers
        def count_nz(r): return sum(1 for row in r for v in row if v != 0)
        sorted_group = sorted(group, key=count_nz, reverse=True)
        key, target = sorted_group[0], sorted_group[-1]
        target_fill = next((v for row in target for v in row if v not in [0, 6, 8]), None)
