def solver(input_grid):
    import numpy as np
    from scipy.ndimage import label

    grid = np.array(input_grid)

    # Find background color (most frequent)
    unique, counts = np.unique(grid, return_counts=True)
    bg = unique[np.argmax(counts)]

    # Find connected components
    mask = grid != bg
    labeled, num_features = label(mask)

    if num_features == 0:
        return input_grid

    # Extract all components
    components = []
    for i in range(1, num_features + 1):
        rows, cols = np.where(labeled == i)
        if len(rows) == 0:
            continue
        min_r, max_r = rows.min(), rows.max()
        min_c, max_c = cols.min(), cols.max()
        sub_grid = np.full((max_r - min_r + 1, max_c - min_c + 1), bg, dtype=grid.dtype)
        for r, c in zip(rows, cols):
            sub_grid[r - min_r, c - min_c] = grid[r, c]
        components.append(sub_grid)

    if len(components) == 1:
        return components[0].tolist()

    # Find target height and pad components
    target_height = max(c.shape[0] for c in components)
    patterns = []
    for comp in components:
        if comp.shape[0] < target_height:
            pad_top = (target_height - comp.shape[0]) // 2
            padded = np.full((target_height, comp.shape[1]), bg, dtype=grid.dtype)
            padded[pad_top:pad_top + comp.shape[0], :] = comp
            patterns.append(padded)
        else:
            patterns.append(comp)

    # Sort by content size (descending)
    patterns.sort(key=lambda p: np.sum(p != bg), reverse=True)

    # Merge patterns
    result = patterns[0].copy()

    for pattern in patterns[1:]:
        best_offset, best_overlap = None, -1

        for offset in range(-pattern.shape[1], result.shape[1] + pattern.shape[1]):
            overlap, valid = 0, True
            for pc in range(pattern.shape[1]):
                rc = pc - offset
                if 0 <= rc < result.shape[1]:
                    for r in range(target_height):
                        pv, rv = pattern[r, pc], result[r, rc]
                        if pv != bg and rv != bg:
                            if pv != rv:
                                valid = False
                                break
                            overlap += 1
                    if not valid:
                        break
            if valid and overlap > best_overlap:
                best_overlap, best_offset = overlap, offset

        if best_offset is not None and best_overlap > 0:
            new_left = min(0, -best_offset)
            new_right = max(result.shape[1], pattern.shape[1] - best_offset)
            new_result = np.full((target_height, new_right - new_left), bg, dtype=grid.dtype)

            for c in range(result.shape[1]):
                new_result[:, c - new_left] = result[:, c]
            for c in range(pattern.shape[1]):
                nc = c - best_offset - new_left
                for r in range(target_height):
                    if pattern[r, c] != bg:
                        new_result[r, nc] = pattern[r, c]
            result = new_result

    return result.tolist()
