def solver(input_grid):
    import numpy as np

    grid = np.array(input_grid, dtype=int)
    H, W = grid.shape

    # Find 5s and compute bounding box
    fives = np.argwhere(grid == 5)
    assert len(fives) >= 3, "Need at least 3 cells with value 5"

    r_min, c_min = fives.min(axis=0)
    r_max, c_max = fives.max(axis=0)
    out_H = r_max - r_min + 1
    out_W = c_max - c_min + 1

    # Pattern mask (non-0, non-5)
    pattern = (grid != 0) & (grid != 5)

    if out_W > out_H:
        # Horizontal extension: find a color block to tile
        row_slice = grid[r_min:r_max+1, :]
        colors = set(row_slice.flatten()) - {0, 5}

        best_block = None
        best_width = 0

        for color in colors:
            color_mask = (row_slice == color)
            color_cols = np.where(np.any(color_mask, axis=0))[0]
            if len(color_cols) == 0:
                continue

            # Find contiguous block width
            width = color_cols[-1] - color_cols[0] + 1

            # Pick block whose width best divides output width
            if out_W % width == 0 and width > best_width:
                best_width = width
                best_block = row_slice[:, color_cols[0]:color_cols[-1]+1].copy()

        if best_block is None:
            raise ValueError("No suitable block found")

        # Tile horizontally: first column stays, remaining columns tile
        output = np.zeros((out_H, out_W), dtype=int)
        output[:, 0] = best_block[:, 0]

        for j in range(1, out_W):
            src_j = 1 + (j - 1) % (best_width - 1) if best_width > 1 else 0
            output[:, j] = best_block[:, src_j]

        return output.tolist()

    else:
        # Vertical extension: extract columns from pattern
        template_cols = []
        for c in range(c_min - 1, -1, -1):
            if np.any(pattern[:, c]):
                template_cols.append(c)
            if len(template_cols) >= out_W:
                break
        template_cols = sorted(template_cols)

        if len(template_cols) == 0:
            raise ValueError("No template columns found")

        # Find template row range
        t_rows = []
        for r in range(H):
            if np.any((grid[r, template_cols] != 0) & (grid[r, template_cols] != 5)):
                t_rows.append(r)

        if len(t_rows) == 0:
            raise ValueError("Empty template")

        t_r_min, t_r_max = min(t_rows), max(t_rows)
        center = (t_r_min + t_r_max) / 2

        # Build output
        output = np.zeros((out_H, out_W), dtype=int)

        for i in range(out_H):
            r = r_min + i
            for j in range(out_W):
                c = template_cols[j] if j < len(template_cols) else template_cols[-1]

                if 0 <= r < H:
                    v = grid[r, c]
                else:
                    v = 0

                # Extrapolate zeros using symmetry
                if v == 0:
                    mirror_r = int(2 * center - r + 0.5)
                    if 0 <= mirror_r < H:
                        v = grid[mirror_r, c]

                    # If still 0, find closest non-zero in same column
                    if v == 0:
                        col_vals = grid[:, c]
                        nz = np.where((col_vals != 0) & (col_vals != 5))[0]
                        if len(nz) > 0:
                            closest = nz[np.argmin(np.abs(nz - r))]
                            v = col_vals[closest]

                output[i, j] = v

        return output.tolist()
