def solver(input_grid: list[list[int]]) -> list[list[int]]:
    """
    Inferred rule (fits all solved training examples):

    - Background color = unique most frequent color.
    - Identify "base objects": 8-connected components of a single non-background color whose
      bounding box contains at least one other non-background color, and whose color is the
      dominant (most frequent) non-background color inside that bounding box.
      If a candidate's bbox is strictly contained within another candidate's bbox, discard it
      (prevents selecting small marker clusters as base objects).

    For each base object:
    - Inside its bounding box, all non-background pixels with colors != base color are "intruders".
    - Split intruders into 4-connected components (per color). Each component contributes ONE
      marker pixel (its own color) to be moved.
    - Remove (set to background) all intruder pixels from inside the base bbox.
    - Determine an output direction based on a "tip" of the base object:
        A side of the base bbox is a tip if the base object touches that side in exactly one pixel.
      Among tip directions that can fit all markers (enough in-bounds empty background cells),
      choose the one with the greatest available space to the grid border; tie-break by
      priority: up, down, left, right.
    - Place the marker pixels in a straight line extending outward from the tip, starting adjacent
      to the bbox. Order markers by closeness to the tip along that axis (deterministic).

    Intruder pixels outside all base bboxes remain unchanged.
    """
    from collections import Counter, deque, defaultdict

    H = len(input_grid)
    if H == 0:
        raise ValueError("Empty grid")
    W = len(input_grid[0])
    if any(len(row) != W for row in input_grid):
        raise ValueError("Non-rectangular grid")

    # ---- Background color ----
    flat = [v for row in input_grid for v in row]
    freq = Counter(flat)
    max_count = max(freq.values())
    bg_candidates = [col for col, cnt in freq.items() if cnt == max_count]
    if len(bg_candidates) != 1:
        raise ValueError(f"Ambiguous background color: candidates={bg_candidates}")
    bg = bg_candidates[0]

    # ---- Connectivity helpers ----
    dirs4 = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    dirs8 = [(dr, dc) for dr in (-1, 0, 1) for dc in (-1, 0, 1) if not (dr == 0 and dc == 0)]

    def bbox_of_points(points):
        rs = [p[0] for p in points]
        cs = [p[1] for p in points]
        return min(rs), max(rs), min(cs), max(cs)

    def components_of_color(color, dirs):
        """Connected components of exactly `color` using neighbor steps in `dirs`."""
        visited = [[False] * W for _ in range(H)]
        comps = []
        for r in range(H):
            for c in range(W):
                if input_grid[r][c] == color and not visited[r][c]:
                    q = deque([(r, c)])
                    visited[r][c] = True
                    comp = []
                    while q:
                        rr, cc = q.popleft()
                        comp.append((rr, cc))
                        for dr, dc in dirs:
                            nr, nc = rr + dr, cc + dc
                            if 0 <= nr < H and 0 <= nc < W and not visited[nr][nc] and input_grid[nr][nc] == color:
                                visited[nr][nc] = True
                                q.append((nr, nc))
                    comps.append(comp)
        return comps

    def non_bg_color_counts_in_bbox(r0, r1, c0, c1):
        cnt = Counter()
        for r in range(r0, r1 + 1):
            row = input_grid[r]
            for c in range(c0, c1 + 1):
                v = row[c]
                if v != bg:
                    cnt[v] += 1
        return cnt

    def rect_contains(outer, inner):
        r0, r1, c0, c1 = outer
        rr0, rr1, cc0, cc1 = inner
        return r0 <= rr0 and rr1 <= r1 and c0 <= cc0 and cc1 <= c1

    def rect_strictly_contains(outer, inner):
        return outer != inner and rect_contains(outer, inner)

    def rect_overlap(a, b):
        ar0, ar1, ac0, ac1 = a
        br0, br1, bc0, bc1 = b
        return not (ar1 < br0 or br1 < ar0 or ac1 < bc0 or bc1 < ac0)

    # ---- Find base object candidates (8-connected, same color) ----
    candidates = []
    for color in list(freq.keys()):
        if color == bg:
            continue
        for comp in components_of_color(color, dirs8):
            r0, r1, c0, c1 = bbox_of_points(comp)
            cnt = non_bg_color_counts_in_bbox(r0, r1, c0, c1)
            if len(cnt) < 2:
                continue  # bbox doesn't contain another non-bg color
            dom_count = max(cnt.values())
            dom_colors = [col for col, n in cnt.items() if n == dom_count]
            if len(dom_colors) != 1:
                raise ValueError(f"Ambiguous dominant color in bbox {(r0,r1,c0,c1)}: {dom_colors}")
            if dom_colors[0] != color:
                continue
            candidates.append({"color": color, "cells": set(comp), "bbox": (r0, r1, c0, c1)})

    # Filter out candidates strictly contained within any other candidate bbox.
    base_objs = []
    for i, cand in enumerate(candidates):
        b = cand["bbox"]
        contained = False
        for j, other in enumerate(candidates):
            if i == j:
                continue
            if rect_strictly_contains(other["bbox"], b):
                contained = True
                break
        if not contained:
            base_objs.append(cand)

    if not base_objs:
        raise ValueError("No base objects found")

    # Ensure base bboxes do not overlap (bbox-based intruder association would be ambiguous).
    for i in range(len(base_objs)):
        for j in range(i + 1, len(base_objs)):
            if rect_overlap(base_objs[i]["bbox"], base_objs[j]["bbox"]):
                raise ValueError(f"Overlapping base bboxes: {base_objs[i]['bbox']} and {base_objs[j]['bbox']}")

    # ---- For each base object, collect intruder components (4-connected) and pixels to clear ----
    to_clear = set()
    for obj in base_objs:
        r0, r1, c0, c1 = obj["bbox"]
        base_color = obj["color"]

        pos_by_color = defaultdict(set)
        for r in range(r0, r1 + 1):
            for c in range(c0, c1 + 1):
                v = input_grid[r][c]
                if v != bg and v != base_color:
                    pos_by_color[v].add((r, c))
                    to_clear.add((r, c))

        intruders = []
        for col, positions in pos_by_color.items():
            remaining = set(positions)
            while remaining:
                start = remaining.pop()
                q = deque([start])
                comp_pts = [start]
                while q:
                    rr, cc = q.popleft()
                    for dr, dc in dirs4:
                        nr, nc = rr + dr, cc + dc
                        if (nr, nc) in remaining:
                            remaining.remove((nr, nc))
                            q.append((nr, nc))
                            comp_pts.append((nr, nc))
                intruders.append({"color": col, "bbox": bbox_of_points(comp_pts)})

        if not intruders:
            raise ValueError("Base object bbox contains other colors but no intruders were collected")
        obj["intruders"] = intruders
        obj["n_markers"] = len(intruders)

    # ---- Apply removals ----
    out = [row[:] for row in input_grid]
    for r, c in to_clear:
        out[r][c] = bg

    # ---- Place markers ----
    prio = {"up": 0, "down": 1, "left": 2, "right": 3}

    for obj in base_objs:
        r0, r1, c0, c1 = obj["bbox"]
        comp = obj["cells"]
        n = obj["n_markers"]

        # Identify tip candidates: exactly one base pixel on that bbox side.
        top = [p for p in comp if p[0] == r0]
        bottom = [p for p in comp if p[0] == r1]
        left = [p for p in comp if p[1] == c0]
        right = [p for p in comp if p[1] == c1]

        tip_candidates = []
        if len(top) == 1:
            tip = top[0]
            space = r0
            dest = [(r0 - 1 - i, tip[1]) for i in range(n)]
            tip_candidates.append(("up", tip, space, dest))
        if len(bottom) == 1:
            tip = bottom[0]
            space = (H - 1) - r1
            dest = [(r1 + 1 + i, tip[1]) for i in range(n)]
            tip_candidates.append(("down", tip, space, dest))
        if len(left) == 1:
            tip = left[0]
            space = c0
            dest = [(tip[0], c0 - 1 - i) for i in range(n)]
            tip_candidates.append(("left", tip, space, dest))
        if len(right) == 1:
            tip = right[0]
            space = (W - 1) - c1
            dest = [(tip[0], c1 + 1 + i) for i in range(n)]
            tip_candidates.append(("right", tip, space, dest))

        if not tip_candidates:
            raise ValueError("No tip side (unique boundary pixel) for a base object")

        feasible = []
        for d, tip, space, dest in tip_candidates:
            ok = True
            for rr, cc in dest:
                if not (0 <= rr < H and 0 <= cc < W):
                    ok = False
                    break
                if out[rr][cc] != bg:
                    ok = False
                    break
            if ok:
                feasible.append((d, tip, space, dest))
        if not feasible:
            raise ValueError("No feasible direction to place markers (blocked or out of bounds)")

        # Choose direction: max available space; tie-break by priority up,down,left,right.
        d, tip, space, dest = max(feasible, key=lambda t: (t[2], -prio[t[0]]))

        intruders = obj["intruders"]
        if d == "up":
            intruders_sorted = sorted(intruders, key=lambda x: (x["bbox"][0], x["bbox"][2], x["color"]))
        elif d == "down":
            intruders_sorted = sorted(intruders, key=lambda x: (-x["bbox"][1], x["bbox"][2], x["color"]))
        elif d == "left":
            intruders_sorted = sorted(intruders, key=lambda x: (x["bbox"][2], x["bbox"][0], x["color"]))
        else:  # right
            intruders_sorted = sorted(intruders, key=lambda x: (-x["bbox"][3], x["bbox"][0], x["color"]))

        if len(intruders_sorted) != n or len(dest) != n:
            raise ValueError("Internal error: marker count mismatch")

        for (rr, cc), intr in zip(dest, intruders_sorted):
            if out[rr][cc] != bg:
                raise ValueError("Marker destination unexpectedly occupied")
            out[rr][cc] = intr["color"]

    return out
