    grid = np.array(input_grid)
    h, w = grid.shape

    def find_boxes():
        """Find all rectangular boxes bounded by a single color."""
        boxes = []

        for boundary_color in range(1, 10):
            for r1 in range(h - 2):
                for c1 in range(w - 2):
                    if grid[r1, c1] != boundary_color:
                        continue

                    # Find right extent of top boundary
                    c2 = c1
                    while c2 + 1 < w and grid[r1, c2 + 1] == boundary_color:
                        c2 += 1

                    if c2 <= c1 + 1:
                        continue

                    # Find bottom extent
                    r2 = r1
                    while r2 + 1 < h and grid[r2 + 1, c1] == boundary_color:
                        r2 += 1

                    if r2 <= r1 + 1:
                        continue

                    # Verify complete rectangle boundary
                    valid = True
                    for c in range(c1, c2 + 1):
                        if grid[r1, c] != boundary_color or grid[r2, c] != boundary_color:
                            valid = False
                            break
                    if valid:
                        for r in range(r1, r2 + 1):
                            if grid[r, c1] != boundary_color or grid[r, c2] != boundary_color:
                                valid = False
                                break

                    if valid:
                        content = grid[r1 + 1:r2, c1 + 1:c2].copy()
                        if content.size > 0:
                            boxes.append({
                                'bounds': (r1, r2, c1, c2),
                                'content': content,
                                'boundary': boundary_color
                            })

        # Remove duplicates
        unique_boxes = []
        seen = set()
        for box in boxes:
            if box['bounds'] not in seen:
                unique_boxes.append(box)
                seen.add(box['bounds'])

        return unique_boxes

    boxes = find_boxes()

    # Filter for candidate boxes with 0 background and markers
