    for box in boxes:
        content = box['content']
        unique = set(np.unique(content))
        non_zero = unique - {0}

        if 0 in unique and len(non_zero) >= 1:
            candidates.append((box, non_zero))

    # Sort by priority: 6 (center marker) > 2 (common marker) > others
    candidates.sort(key=lambda x: (
        0 if 6 in x[1] else (1 if 2 in x[1] else 2),
        -len(x[1])
    ))

    assert candidates, "No candidate boxes found"

    best_box, non_zero = candidates[0]
    content = best_box['content'].copy()

    # Apply transformation based on marker presence
    if 6 in non_zero:
        # Draw lines from center marker (6) to endpoint markers
        center_marker = 6
        centers = np.argwhere(content == center_marker)

        for marker in non_zero - {6}:
            endpoints = np.argwhere(content == marker)
            for cr, cc in centers:
                for er, ec in endpoints:
                    if cr == er:  # Horizontal line
                        for c in range(min(cc, ec), max(cc, ec) + 1):
                            if content[cr, c] == 0:
                                content[cr, c] = marker
                    elif cc == ec:  # Vertical line
                        for r in range(min(cr, er), max(cr, er) + 1):
                            if content[r, cc] == 0:
                                content[r, cc] = marker
    else:
        # Complement transformation: swap 0 and marker color
        marker = 2 if 2 in non_zero else min(non_zero)
        result = content.copy()
        result[content == 0] = marker
        result[content == marker] = 0
        content = result

    return content.tolist()
