def solver(input_grid):
    import numpy as np
    from collections import deque

    grid = np.array(input_grid)
    H, W = grid.shape

    # Find background color (most common)
    unique, counts = np.unique(grid, return_counts=True)
    bg = unique[np.argmax(counts)]

    # Find connected components of non-background cells
    visited = np.zeros((H, W), dtype=bool)
    comps = []
    for r in range(H):
        for c in range(W):
            if grid[r, c] != bg and not visited[r, c]:
                comp = []
                q = deque([(r, c)])
                visited[r, c] = True
                while q:
                    cr, cc = q.popleft()
                    comp.append((cr, cc))
                    for dr, dc in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                        nr, nc = cr + dr, cc + dc
                        if 0 <= nr < H and 0 <= nc < W and not visited[nr, nc] and grid[nr, nc] != bg:
                            visited[nr, nc] = True
                            q.append((nr, nc))
                comps.append(comp)

    if not comps:
        return input_grid

    # Extract fragments (bounding boxes)
    frags = []
    for comp in comps:
        rows = [r for r, c in comp]
        cols = [c for r, c in comp]
        r_min, r_max = min(rows), max(rows)
        c_min, c_max = min(cols), max(cols)
        frags.append(grid[r_min:r_max+1, c_min:c_max+1].copy())

    # Pad all fragments to the same (maximum) height
    max_h = max(f.shape[0] for f in frags)
    for i, f in enumerate(frags):
        if f.shape[0] < max_h:
            diff = max_h - f.shape[0]
            top = diff // 2
            new_f = np.full((max_h, f.shape[1]), bg, dtype=f.dtype)
            new_f[top:top + f.shape[0], :] = f
            frags[i] = new_f

    if len(frags) == 1:
        return frags[0].tolist()

    n = len(frags)

    # Build edge relationships: edges[i] = (j, splice_col) means fragment j follows fragment i,
    # with the splice point at column splice_col of fragment i
    def col_as_tuple(f, c):
        return tuple(f[:, c].tolist())

    edges = {}
    for i in range(n):
        for j in range(n):
            if i != j and i not in edges:
                target = col_as_tuple(frags[j], 0)  # First column of j
                for c in range(frags[i].shape[1]):
                    if col_as_tuple(frags[i], c) == target:
                        edges[i] = (j, c)
                        break

    # Find starting fragments (those that are not successors of any other fragment)
    successors = set(j for j, _ in edges.values())
    starts = [i for i in range(n) if i not in successors]
    if not starts:
        starts = list(range(n))

    # Build the longest chain
    best_chain = []
    for start in starts:
        chain = [start]
        cur = start
        while cur in edges:
            nxt, _ = edges[cur]
            if nxt in chain:
                break
            chain.append(nxt)
            cur = nxt
        if len(chain) > len(best_chain):
            best_chain = chain

    if not best_chain:
        return frags[0].tolist()

    # Build the result by concatenating fragments with appropriate column ranges
    parts = []
    for i, frag_idx in enumerate(best_chain):
        f = frags[frag_idx]
        start_col = 0 if i == 0 else 1  # First fragment uses all cols; others skip the overlapping first col
        if frag_idx in edges:
            _, splice_col = edges[frag_idx]
            end_col = splice_col + 1  # Include up to and including the splice column
        else:
            end_col = f.shape[1]  # Last fragment in chain uses all remaining cols
        parts.append(f[:, start_col:end_col])

    result = np.hstack(parts)
    return result.tolist()
