def solver(input_grid):
    import numpy as np
    from scipy.ndimage import label
    from itertools import permutations

    grid = np.array(input_grid)
    H, W = grid.shape

    # Find background (most frequent color)
    colors, counts = np.unique(grid, return_counts=True)
    bg = int(colors[np.argmax(counts)])

    # Find connected components
    mask = (grid != bg).astype(int)
    labeled, num_features = label(mask)

    # Extract regions with their bounding boxes
    regions = []
    for i in range(1, num_features + 1):
        ys, xs = np.where(labeled == i)
        if len(ys) == 0:
            continue
        y0, y1 = ys.min(), ys.max() + 1
        x0, x1 = xs.min(), xs.max() + 1
        regions.append(grid[y0:y1, x0:x1].copy())

    if not regions:
        return input_grid

    # Remove subpatterns (smaller patterns that appear inside larger ones)
    def is_subpattern(small, large):
        sh, sw = small.shape
        lh, lw = large.shape
        if sh > lh or sw > lw:
            return False
        for dy in range(lh - sh + 1):
            for dx in range(lw - sw + 1):
                if np.array_equal(small, large[dy:dy+sh, dx:dx+sw]):
                    return True
        return False

    regions.sort(key=lambda r: -r.size)
    unique = []
    for r in regions:
        if not any(is_subpattern(r, u) for u in unique):
            unique.append(r)

    if len(unique) == 1:
        return unique[0].tolist()

    # Pad to same height (centered vertically)
    max_h = max(r.shape[0] for r in unique)
    padded = []
    for r in unique:
        h = r.shape[0]
        if h < max_h:
            top = (max_h - h) // 2
            bot = max_h - h - top
            r = np.pad(r, ((top, bot), (0, 0)), constant_values=bg)
        padded.append(r)

    def overlay_merge(a, b, bg, max_h):
        """Find best alignment where b overlays to the right of a."""
        best_offset = a.shape[1]
        best_width = a.shape[1] + b.shape[1]

        for offset in range(a.shape[1] + b.shape[1]):
            conflicts = False
            for col in range(b.shape[1]):
                combined_col = offset + col
                if 0 <= combined_col < a.shape[1]:
                    for row in range(max_h):
                        a_val = a[row, combined_col]
                        b_val = b[row, col]
                        if a_val != bg and b_val != bg and a_val != b_val:
                            conflicts = True
                            break
                    if conflicts:
                        break

            if not conflicts:
                result_width = max(a.shape[1], offset + b.shape[1])
                if result_width < best_width:
                    best_width = result_width
                    best_offset = offset

        result = np.full((max_h, best_width), bg, dtype=int)
        result[:, :a.shape[1]] = a
        for col in range(b.shape[1]):
            combined_col = best_offset + col
            if combined_col < best_width:
                for row in range(max_h):
                    if b[row, col] != bg:
                        result[row, combined_col] = b[row, col]

        return result

    # Try all permutations to find optimal merge order
    best_output = None
    best_width = float('inf')

    for perm in permutations(range(len(padded))):
        output = padded[perm[0]].copy()
        for i in range(1, len(perm)):
            output = overlay_merge(output, padded[perm[i]], bg, max_h)
        if output.shape[1] < best_width:
            best_width = output.shape[1]
            best_output = output.copy()

    return best_output.tolist()
