def solver(input_grid: list[list[int]]) -> list[list[int]]:
    """
    Learned rule (fits all solved examples):
    - Background is the most frequent color.
    - There is one "marker" color (the only non-background color; optionally the input may already contain fill color 2).
    - Marker pixels form one or more 8-connected diagonal line components of slope -1 (constant u = r+c).
    - Treat each such diagonal component as a *continuous* segment in (u=r+c, v=r-c) space:
        * for each constant-u run, it spans all integer v between its endpoints (even if no pixel exists for some parities).
    - For each v (i.e., for each NW-SE diagonal, constant r-c), find all marker-segments that span that v.
      If at least two segments span it, fill every background cell whose u lies strictly between the minimum and maximum
      such u values with color 2.
    - Keep all non-background cells unchanged.
    """
    from collections import Counter, defaultdict, deque

    h = len(input_grid)
    assert h > 0, "empty grid"
    w = len(input_grid[0])
    assert all(len(row) == w for row in input_grid), "non-rectangular grid"

    counts = Counter(v for row in input_grid for v in row)
    bg = counts.most_common(1)[0][0]
    fill = 2
    assert fill != bg, "fill color equals background; rule would be ambiguous"

    colors = set(counts.keys())
    non_bg = colors - {bg}

    if len(non_bg) == 1:
        marker = next(iter(non_bg))
    elif len(non_bg) == 2 and fill in non_bg:
        marker = next(iter(non_bg - {fill}))
    else:
        raise ValueError(
            f"expected exactly one marker color besides background (optionally plus fill=2); got non_bg={sorted(non_bg)}"
        )

    marker_set = {(r, c) for r in range(h) for c in range(w) if input_grid[r][c] == marker}
    assert marker_set, "no marker pixels found"

    # Preconditions: each 8-connected component of marker pixels lies on a single anti-diagonal (constant u=r+c).
    neigh8 = [(dr, dc) for dr in (-1, 0, 1) for dc in (-1, 0, 1) if not (dr == 0 and dc == 0)]
    visited = set()
    for start in marker_set:
        if start in visited:
            continue
        u0 = start[0] + start[1]
        q = deque([start])
        visited.add(start)
        while q:
            r, c = q.popleft()
            if r + c != u0:
                raise ValueError("marker component is not a single slope -1 diagonal (not constant r+c)")
            for dr, dc in neigh8:
                rr, cc = r + dr, c + dc
                if (rr, cc) in marker_set and (rr, cc) not in visited:
                    visited.add((rr, cc))
                    q.append((rr, cc))

    # Build diagonal segments in (u=r+c, v=r-c) space.
    # For each u, there may be multiple disjoint runs (separated by gaps along the diagonal).
    by_u_rows = defaultdict(list)
    for r, c in marker_set:
        by_u_rows[r + c].append(r)

    v_to_us = defaultdict(list)  # v -> list of u's of segments that span this v (with continuous densification)
    for u, rs in by_u_rows.items():
        rs_sorted = sorted(rs)
        run_start = rs_sorted[0]
        prev = rs_sorted[0]
        for r in rs_sorted[1:] + [None]:
            if r is not None and r == prev + 1:
                prev = r
                continue

            # finalize run [run_start, prev]
            v_min = 2 * run_start - u
            v_max = 2 * prev - u
            if v_min > v_max:
                v_min, v_max = v_max, v_min
            for v in range(v_min, v_max + 1):
                v_to_us[v].append(u)

            if r is None:
                break
            run_start = prev = r

    envelope = {}
    for v, us in v_to_us.items():
        if len(us) >= 2:
            lo = min(us)
            hi = max(us)
            if lo != hi:
                envelope[v] = (lo, hi)

    out = [row[:] for row in input_grid]
    for r in range(h):
        for c in range(w):
            if input_grid[r][c] != bg:
                continue  # keep marker (and any other non-background colors) unchanged
            v = r - c
            if v not in envelope:
                continue
            lo, hi = envelope[v]
            u = r + c
            if lo < u < hi:
                out[r][c] = fill

    return out
