def solver(input_grid: list[list[int]]) -> list[list[int]]:
    """
    Learned rule from training examples:

    - Determine background as the unique most frequent color.
    - For each non-background color c:
        1) Remove "diagonal-only" pixels of color c:
           pixels that have at least one diagonal neighbor of color c but
           have *no* orthogonal (4-neighborhood) neighbor of color c.
        2) Fill length-3 gaps (distance 4) along rows/columns between two pixels of color c
           *only if* at least one endpoint is diagonally adjacent to a removed diagonal-only pixel.
           (Fill the 3 intervening background cells with color c.)

    This matches all provided solved examples and uses probes only for generality checks.
    """
    from collections import Counter

    # --- Basic validation ---
    assert isinstance(input_grid, list) and input_grid, "input_grid must be a non-empty list of rows"
    h = len(input_grid)
    w = len(input_grid[0])
    assert w > 0, "input_grid must have at least one column"
    assert all(isinstance(row, list) and len(row) == w for row in input_grid), "input_grid must be rectangular"

    # --- Determine background color (unique most frequent) ---
    counts = Counter()
    for row in input_grid:
        counts.update(row)
    assert counts, "grid has no cells"

    max_freq = max(counts.values())
    bg_candidates = [c for c, cnt in counts.items() if cnt == max_freq]
    assert len(bg_candidates) == 1, "Ambiguous background: most frequent color is not unique"
    bg = bg_candidates[0]

    fg_colors = [c for c in counts.keys() if c != bg]
    assert fg_colors, "No foreground colors to process"

    ortho = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    diag = [(-1, -1), (-1, 1), (1, -1), (1, 1)]

    # --- Step 1: find diagonal-only pixels per color (based on the original input) ---
    diag_only_by_color: dict[int, set[tuple[int, int]]] = {c: set() for c in fg_colors}

    for r in range(h):
        row = input_grid[r]
        for c in range(w):
            col = row[c]
            if col == bg:
                continue

            has_ortho = False
            for dr, dc in ortho:
                rr, cc = r + dr, c + dc
                if 0 <= rr < h and 0 <= cc < w and input_grid[rr][cc] == col:
                    has_ortho = True
                    break
            if has_ortho:
                continue

            has_diag = False
            for dr, dc in diag:
                rr, cc = r + dr, c + dc
                if 0 <= rr < h and 0 <= cc < w and input_grid[rr][cc] == col:
                    has_diag = True
                    break
            if has_diag:
                diag_only_by_color[col].add((r, c))

    # --- Step 2: remove diagonal-only pixels ---
    out = [row[:] for row in input_grid]
    for col, cells in diag_only_by_color.items():
        for r, c in cells:
            out[r][c] = bg

    # Helper: does this endpoint touch (diagonally) a removed diagonal-only pixel of the same color?
    def endpoint_touches_removed_diagonal(r: int, c: int, col: int) -> bool:
        diagset = diag_only_by_color.get(col)
        if not diagset:
            return False
        for dr, dc in diag:
            rr, cc = r + dr, c + dc
            if (rr, cc) in diagset:
                return True
        return False

    # --- Step 3: fill length-3 gaps (distance 4) gated by diagonal-only adjacency ---
    fills: dict[tuple[int, int], int] = {}

    # Horizontal gaps
    for col in fg_colors:
        for r in range(h):
            for c in range(w - 4):
                if out[r][c] != col or out[r][c + 4] != col:
                    continue
                if out[r][c + 1] != bg or out[r][c + 2] != bg or out[r][c + 3] != bg:
                    continue
                if not (endpoint_touches_removed_diagonal(r, c, col) or endpoint_touches_removed_diagonal(r, c + 4, col)):
                    continue
                for cc in (c + 1, c + 2, c + 3):
                    key = (r, cc)
                    prev = fills.get(key)
                    if prev is not None and prev != col:
                        raise ValueError("Fill conflict: two colors want to fill the same cell")
                    fills[key] = col

    # Vertical gaps
    for col in fg_colors:
        for c in range(w):
            for r in range(h - 4):
                if out[r][c] != col or out[r + 4][c] != col:
                    continue
                if out[r + 1][c] != bg or out[r + 2][c] != bg or out[r + 3][c] != bg:
                    continue
                if not (endpoint_touches_removed_diagonal(r, c, col) or endpoint_touches_removed_diagonal(r + 4, c, col)):
                    continue
                for rr in (r + 1, r + 2, r + 3):
                    key = (rr, c)
                    prev = fills.get(key)
                    if prev is not None and prev != col:
                        raise ValueError("Fill conflict: two colors want to fill the same cell")
                    fills[key] = col

    # Apply fills
    for (r, c), col in fills.items():
        assert out[r][c] == bg, "Expected background at fill position"
        out[r][c] = col

    return out

