def solver(input_grid: list[list[int]]) -> list[list[int]]:
    import numpy as np
    from collections import Counter

    arr = np.array(input_grid, dtype=int)
    h, w = arr.shape

    counts = Counter(arr.ravel().tolist())
    if len(counts) <= 1:
        # Single-color grid: nothing to straighten/clean.
        return [row[:] for row in input_grid]

    bg, bg_count = counts.most_common(1)[0]
    second_count = counts.most_common(2)[1][1]
    assert bg_count > second_count, "Background color not uniquely most frequent; rule needs a clear background."

    out = arr.copy()

    # Process each non-background color independently.
    for color in sorted([c for c in counts if c != bg]):
        # --- 1) Detect "diagonal-only" pixels: diagonally connected but with no 4-neighbor of same color.
        diag_only = set()
        for r in range(h):
            for c in range(w):
                if arr[r, c] != color:
                    continue

                orth = 0
                if r > 0 and arr[r - 1, c] == color:
                    orth += 1
                if r + 1 < h and arr[r + 1, c] == color:
                    orth += 1
                if c > 0 and arr[r, c - 1] == color:
                    orth += 1
                if c + 1 < w and arr[r, c + 1] == color:
                    orth += 1
                if orth != 0:
                    continue

                diag = 0
                if r > 0 and c > 0 and arr[r - 1, c - 1] == color:
                    diag += 1
                if r > 0 and c + 1 < w and arr[r - 1, c + 1] == color:
                    diag += 1
                if r + 1 < h and c > 0 and arr[r + 1, c - 1] == color:
                    diag += 1
                if r + 1 < h and c + 1 < w and arr[r + 1, c + 1] == color:
                    diag += 1

                if diag > 0:
                    diag_only.add((r, c))

        if not diag_only:
            continue

        def run_lengths(r: int, c: int) -> tuple[int, int]:
            """Contiguous run lengths through (r,c) in original grid along row/col."""
            hl = 1
            cc = c - 1
            while cc >= 0 and arr[r, cc] == color:
                hl += 1
                cc -= 1
            cc = c + 1
            while cc < w and arr[r, cc] == color:
                hl += 1
                cc += 1

            vl = 1
            rr = r - 1
            while rr >= 0 and arr[rr, c] == color:
                vl += 1
                rr -= 1
            rr = r + 1
            while rr < h and arr[rr, c] == color:
                vl += 1
                rr += 1

            return hl, vl

        # --- 2) Group diagonal-only pixels by diagonal adjacency and project them onto a nearby straight line.
        visited = set()
        add_set = set()          # projected pixels to add
        activated_rows = set()   # rows where we will fill tiny gaps (<=2) to complete straightening
        activated_cols = set()   # columns where we will fill tiny gaps (<=2)

        diag_dirs = [(-1, -1), (-1, 1), (1, -1), (1, 1)]

        for start in list(diag_only):
            if start in visited:
                continue

            # BFS over diagonal adjacency (only diagonal moves are possible among diag_only by definition).
            stack = [start]
            visited.add(start)
            comp = []
            while stack:
                r, c = stack.pop()
                comp.append((r, c))
                for dr, dc in diag_dirs:
                    nr, nc = r + dr, c + dc
                    if (nr, nc) in diag_only and (nr, nc) not in visited:
                        visited.add((nr, nc))
                        stack.append((nr, nc))

            # Find anchors: diagonal neighbors of comp pixels that are same color but NOT diagonal-only.
            anchors = set()
            for r, c in comp:
                for dr, dc in diag_dirs:
                    nr, nc = r + dr, c + dc
                    if 0 <= nr < h and 0 <= nc < w and arr[nr, nc] == color and (nr, nc) not in diag_only:
                        anchors.add((nr, nc))

            if not anchors:
                # Pure diagonal noise: remove it; no projection / line completion.
                continue

            comp_rows = [r for r, _ in comp]
            comp_cols = [c for _, c in comp]

            # Choose best anchor and whether to project horizontally or vertically.
            # We choose the option (h or v) that minimizes total movement; tie-break by longer run.
            best = None
            best_anchor = None  # (ar, ac, orient)
            for ar, ac in anchors:
                hl, vl = run_lengths(ar, ac)
                cost_h = sum(abs(r - ar) for r in comp_rows)  # if projecting to row ar
                cost_v = sum(abs(c - ac) for c in comp_cols)  # if projecting to col ac

                score_h = (cost_h, -hl, ar, ac, "h")
                score_v = (cost_v, -vl, ar, ac, "v")
                score = score_h if score_h <= score_v else score_v

                if best is None or score < best:
                    best = score
                    best_anchor = (ar, ac, score[-1])

            ar, ac, orient = best_anchor
            if orient == "h":
                activated_rows.add(ar)
                for r, c in comp:
                    add_set.add((ar, c))
            else:
                activated_cols.add(ac)
                for r, c in comp:
                    add_set.add((r, ac))

        # --- 3) Apply removals (diagonal-only pixels) and additions (projected pixels).
        for r, c in diag_only:
            if out[r, c] == color:
                out[r, c] = bg

        for r, c in add_set:
            if out[r, c] != color:
                assert out[r, c] == bg, "Projection would overwrite a different non-background color."
                out[r, c] = color

        # --- 4) Fill tiny gaps (length 1 or 2) only on lines we activated, and only when at least
        #         one endpoint of the gap is a projected pixel (prevents over-connecting unrelated parts).
        for r in activated_rows:
            cols = [c for c in range(w) if out[r, c] == color]
            cols.sort()
            for i in range(len(cols) - 1):
                c1, c2 = cols[i], cols[i + 1]
                gap = c2 - c1 - 1
                if 1 <= gap <= 2 and ((r, c1) in add_set or (r, c2) in add_set):
                    for cc in range(c1 + 1, c2):
                        if out[r, cc] != color:
                            assert out[r, cc] == bg, "Row gap-fill would overwrite a different non-background color."
                            out[r, cc] = color

        for c in activated_cols:
            rows = [r for r in range(h) if out[r, c] == color]
            rows.sort()
            for i in range(len(rows) - 1):
                r1, r2 = rows[i], rows[i + 1]
                gap = r2 - r1 - 1
                if 1 <= gap <= 2 and ((r1, c) in add_set or (r2, c) in add_set):
                    for rr in range(r1 + 1, r2):
                        if out[rr, c] != color:
                            assert out[rr, c] == bg, "Column gap-fill would overwrite a different non-background color."
                            out[rr, c] = color

    return out.tolist()
