def solver(input_grid: list[list[int]]) -> list[list[int]]:
    """
    Learned rule from training examples:

    - Identify one or more "instruction panels": rectangular bordered components of a single color C
      (C not in {background, token, 1, 2}) whose bounding box border is entirely C and whose interior
      contains both colors 1 and 2 (and otherwise only C and background).
    - Each such panel defines a movement direction for its color C:
        direction = sign( centroid(2s) - centroid(1s) )  (per axis), yielding one of 8 compass directions.
    - Tokens of color 4 ("moving pieces") slide in the corresponding direction until blocked, like gravity:
        * If there is only one panel color, all tokens move in that single direction.
        * If multiple panel colors exist, each token is associated to exactly one panel color C by:
            along direction(C), the first non-background obstacle encountered (ignoring other tokens)
            must be color C.
      Then tokens for each C move simultaneously with stacking (cannot overlap), and do not interact across
      different C groups (we assert no cross-group collisions).
    - Only token positions change; everything else stays the same.
    """
    import numpy as np
    from collections import deque, defaultdict

    grid = np.array(input_grid, dtype=int)
    H, W = grid.shape

    # --- Helpers ---
    def fail(msg: str):
        raise ValueError(msg)

    def in_bounds(r: int, c: int) -> bool:
        return 0 <= r < H and 0 <= c < W

    def sign(x: float) -> int:
        return 1 if x > 0 else (-1 if x < 0 else 0)

    # --- Determine background (most frequent color) ---
    vals, counts = np.unique(grid, return_counts=True)
    bg = int(vals[int(np.argmax(counts))])

    # --- Token color is fixed by training: 4 ---
    token = 4
    if token not in set(int(v) for v in vals):
        fail("Expected moving token color 4 to be present.")

    token_positions = list(zip(*np.where(grid == token)))
    if not token_positions:
        fail("No tokens (color 4) found.")

    # Grid with tokens removed (treated as empty for obstacle/assignment computations)
    base = grid.copy()
    for r, c in token_positions:
        base[r, c] = bg

    # --- Find instruction panels and directions per panel color ---
    # Panel colors exclude background, token, and marker colors 1 and 2.
    candidate_colors = {int(c) for c in vals if int(c) not in (bg, token, 1, 2)}
    visited = np.zeros((H, W), dtype=bool)
    panel_dirs: dict[int, tuple[int, int]] = {}

    for r0 in range(H):
        for c0 in range(W):
            col = int(grid[r0, c0])
            if col not in candidate_colors or visited[r0, c0]:
                continue

            # BFS component for this color
            q = deque([(r0, c0)])
            visited[r0, c0] = True
            minr = maxr = r0
            minc = maxc = c0

            while q:
                r, c = q.popleft()
                if r < minr:
                    minr = r
                if r > maxr:
                    maxr = r
                if c < minc:
                    minc = c
                if c > maxc:
                    maxc = c

                for dr, dc in ((1, 0), (-1, 0), (0, 1), (0, -1)):
                    rr, cc = r + dr, c + dc
                    if in_bounds(rr, cc) and (not visited[rr, cc]) and int(grid[rr, cc]) == col:
                        visited[rr, cc] = True
                        q.append((rr, cc))

            bh = maxr - minr + 1
            bw = maxc - minc + 1
            if bh < 3 or bw < 3:
                continue

            # Border must be entirely the component color
            if not np.all(grid[minr, minc:maxc + 1] == col):
                continue
            if not np.all(grid[maxr, minc:maxc + 1] == col):
                continue
            if not np.all(grid[minr:maxr + 1, minc] == col):
                continue
            if not np.all(grid[minr:maxr + 1, maxc] == col):
                continue

            sub = grid[minr:maxr + 1, minc:maxc + 1]

            # Allow either solid or framed panels: inside may include background, but no other colors.
            allowed = np.array([col, 1, 2, bg], dtype=int)
            if not np.all(np.isin(sub, allowed)):
                continue
            if not (np.any(sub == 1) and np.any(sub == 2)):
                continue

            # Compute direction from centroid of 1s to centroid of 2s
            coords1 = np.argwhere(sub == 1).astype(float)
            coords2 = np.argwhere(sub == 2).astype(float)
            c1 = coords1.mean(axis=0)
            c2 = coords2.mean(axis=0)
            dr = sign(c2[0] - c1[0])
            dc = sign(c2[1] - c1[1])
            if (dr, dc) == (0, 0):
                fail(f"Panel of color {col} has zero direction (centroids coincide).")

            if col in panel_dirs:
                if panel_dirs[col] != (dr, dc):
                    fail(f"Multiple panels for color {col} imply different directions: {panel_dirs[col]} vs {(dr, dc)}")
            else:
                panel_dirs[col] = (dr, dc)

    if not panel_dirs:
        fail("No instruction panels detected (no color-rectangle containing both 1 and 2).")

    # --- Assign tokens to panel colors (if needed) ---
    tokens_by_color: dict[int, list[tuple[int, int]]] = defaultdict(list)

    if len(panel_dirs) == 1:
        # Single direction: all tokens move that way (cannot infer/require target wall color).
        only_color = next(iter(panel_dirs.keys()))
        tokens_by_color[only_color] = token_positions[:]
    else:
        # Multiple directions: each token must match exactly one panel color by its first obstacle.
        for (r, c) in token_positions:
            compatible = []
            for col, (dr, dc) in panel_dirs.items():
                rr, cc = r + dr, c + dc
                while in_bounds(rr, cc):
                    v = int(base[rr, cc])
                    if v != bg:
                        if v == col:
                            compatible.append(col)
                        break
                    rr += dr
                    cc += dc
            if len(compatible) != 1:
                fail(f"Token at {(r, c)} has ambiguous/no controlling panel color: {compatible}")
            tokens_by_color[compatible[0]].append((r, c))

    # --- Simulate sliding for each color-group independently (stacking within a group) ---
    final_positions = []
    used_positions = set()

    for col, toks in tokens_by_color.items():
        if col not in panel_dirs:
            fail(f"Internal error: token group assigned to color {col} with no direction.")
        dr, dc = panel_dirs[col]

        # Order tokens from nearest-to-destination to farthest, for correct stacking.
        # Use projection onto direction vector.
        toks_sorted = sorted(
            toks,
            key=lambda p: (p[0] * dr + p[1] * dc, p[0], p[1]),
            reverse=True
        )

        occupied = set()
        for (r, c) in toks_sorted:
            rr, cc = r, c
            while True:
                nr, nc = rr + dr, cc + dc
                if not in_bounds(nr, nc):
                    break
                if int(base[nr, nc]) != bg:
                    break
                if (nr, nc) in occupied:
                    break
                rr, cc = nr, nc

            if (rr, cc) in used_positions:
                fail("Collision between independently moved groups (unexpected by learned rule).")
            occupied.add((rr, cc))
            used_positions.add((rr, cc))
            final_positions.append((rr, cc))

    # --- Build output: base grid + moved tokens ---
    out = base.copy()
    for r, c in final_positions:
        if int(out[r, c]) != bg:
            fail("Token final position is not background; would overwrite existing content.")
        out[r, c] = token

    return out.tolist()

