def solver(input_grid: list[list[int]]) -> list[list[int]]:
    import numpy as np
    from collections import Counter

    grid = np.array(input_grid, dtype=int)
    H, W = grid.shape

    # Background: most frequent color (verified from the grid; do not assume 0).
    bg = Counter(grid.ravel().tolist()).most_common(1)[0][0]

    MARKER = 4
    DIGIT1, DIGIT2 = 1, 2

    def sgn(x: float) -> int:
        return (x > 0) - (x < 0)

    # ------------------------------------------------------------
    # Detect instruction panels at the four corners.
    #
    # Each instruction panel is assumed to:
    #   - touch a corner,
    #   - be filled on the border with a "border color" B (not bg, not 1/2/4),
    #   - contain at least one 1 and one 2.
    #
    # The movement direction for border color B is:
    #   dir(B) = sign(centroid(2) - centroid(1)) within that panel rectangle.
    # ------------------------------------------------------------
    dir_map: dict[int, tuple[int, int]] = {}

    def add_direction(border_color: int, dr: int, dc: int) -> None:
        if (dr, dc) == (0, 0):
            raise ValueError("Instruction panel produced a zero direction vector")
        if border_color in dir_map:
            if dir_map[border_color] != (dr, dc):
                raise ValueError(f"Conflicting directions for border color {border_color}: "
                                 f"{dir_map[border_color]} vs {(dr, dc)}")
        else:
            dir_map[border_color] = (dr, dc)

    def corner_panel_bbox(r0: int, c0: int) -> tuple[int, int, int, int] | None:
        """Return (r1, r2, c1, c2) for the maximal solid-color rectangle anchored at the corner,
        inferred by contiguous run lengths along the two touching edges."""
        bc = int(grid[r0, c0])
        if bc in (bg, MARKER, DIGIT1, DIGIT2):
            return None

        # Measure width and height from the edges meeting at the corner.
        if r0 == 0 and c0 == 0:
            # top-left: rightwards on top row, downwards on left column
            width = 0
            while c0 + width < W and int(grid[r0, c0 + width]) == bc:
                width += 1
            height = 0
            while r0 + height < H and int(grid[r0 + height, c0]) == bc:
                height += 1
            r1, r2 = r0, r0 + height - 1
            c1, c2 = c0, c0 + width - 1
        elif r0 == 0 and c0 == W - 1:
            # top-right: leftwards on top row, downwards on right column
            width = 0
            while c0 - width >= 0 and int(grid[r0, c0 - width]) == bc:
                width += 1
            height = 0
            while r0 + height < H and int(grid[r0 + height, c0]) == bc:
                height += 1
            r1, r2 = r0, r0 + height - 1
            c2 = c0
            c1 = c0 - width + 1
        elif r0 == H - 1 and c0 == 0:
            # bottom-left: rightwards on bottom row, upwards on left column
            width = 0
            while c0 + width < W and int(grid[r0, c0 + width]) == bc:
                width += 1
            height = 0
            while r0 - height >= 0 and int(grid[r0 - height, c0]) == bc:
                height += 1
            r2 = r0
            r1 = r0 - height + 1
            c1, c2 = c0, c0 + width - 1
        elif r0 == H - 1 and c0 == W - 1:
            # bottom-right: leftwards on bottom row, upwards on right column
            width = 0
            while c0 - width >= 0 and int(grid[r0, c0 - width]) == bc:
                width += 1
            height = 0
            while r0 - height >= 0 and int(grid[r0 - height, c0]) == bc:
                height += 1
            r2 = r0
            r1 = r0 - height + 1
            c2 = c0
            c1 = c0 - width + 1
        else:
            return None

        if r1 < 0 or c1 < 0 or r2 >= H or c2 >= W or r1 > r2 or c1 > c2:
            return None
        return (r1, r2, c1, c2)

    for (r0, c0) in [(0, 0), (0, W - 1), (H - 1, 0), (H - 1, W - 1)]:
        bbox = corner_panel_bbox(r0, c0)
        if bbox is None:
            continue
        r1, r2, c1, c2 = bbox
        box = grid[r1:r2 + 1, c1:c2 + 1]
        coords1 = np.argwhere(box == DIGIT1)
        coords2 = np.argwhere(box == DIGIT2)
        if coords1.size == 0 or coords2.size == 0:
            continue

        # Convert to global coordinates for centroid computation.
        coords1 = coords1 + np.array([r1, c1])
        coords2 = coords2 + np.array([r1, c1])

        m1 = coords1.mean(axis=0)
        m2 = coords2.mean(axis=0)
        dr = sgn(float(m2[0] - m1[0]))
        dc = sgn(float(m2[1] - m1[1]))

        border_color = int(grid[r0, c0])
        add_direction(border_color, dr, dc)

    if not dir_map:
        raise ValueError("No instruction panel detected at any corner (need at least one with colors 1 and 2)")

    # ------------------------------------------------------------
    # Move all marker (4) pixels.
    #
    # For each marker:
    #   - select the border color whose pixels are closest (Manhattan distance),
    #   - slide the marker along that border color's direction through background
    #     until the next step would hit a non-background cell or go out of bounds.
    # Movement is computed on a grid where all markers are removed first (simultaneous move).
    # ------------------------------------------------------------
    marker_positions = [tuple(p) for p in np.argwhere(grid == MARKER)]
    if not marker_positions:
        # Nothing to move; return an explicit copy.
        return [row[:] for row in input_grid]

    base = grid.copy()
    base[base == MARKER] = bg  # remove markers for simultaneous movement

    # Precompute coordinates of each border color for distance queries.
    border_coords: dict[int, list[tuple[int, int]]] = {}
    for bc in dir_map:
        coords = [tuple(p) for p in np.argwhere(grid == bc)]
        if not coords:
            raise ValueError(f"Border color {bc} has a direction but no pixels in the grid")
        border_coords[bc] = coords

    bcs = list(dir_map.keys())

    def min_manhattan_to_color(r: int, c: int, coords: list[tuple[int, int]]) -> int:
        return min(abs(r - rr) + abs(c - cc) for rr, cc in coords)

    def first_obstacle_color(r: int, c: int, dr: int, dc: int) -> int | None:
        rr, cc = r, c
        while True:
            nr, nc = rr + dr, cc + dc
            if nr < 0 or nr >= H or nc < 0 or nc >= W:
                return None
            v = int(base[nr, nc])
            if v != bg:
                return v
            rr, cc = nr, nc

    def choose_border_color_for_marker(r: int, c: int) -> int:
        if len(bcs) == 1:
            return bcs[0]

        dists = {bc: min_manhattan_to_color(r, c, border_coords[bc]) for bc in bcs}
        md = min(dists.values())
        cand = [bc for bc, d in dists.items() if d == md]
        if len(cand) == 1:
            return cand[0]

        # Tie-break: prefer the one whose direction hits its own border color first (when possible).
        match = []
        for bc in cand:
            dr, dc = dir_map[bc]
            if first_obstacle_color(r, c, dr, dc) == bc:
                match.append(bc)
        if len(match) == 1:
            return match[0]

        raise ValueError(f"Ambiguous border-color assignment for marker at {(r, c)}")

    def slide_destination(r: int, c: int, dr: int, dc: int) -> tuple[int, int]:
        rr, cc = r, c
        while True:
            nr, nc = rr + dr, cc + dc
            if nr < 0 or nr >= H or nc < 0 or nc >= W:
                return rr, cc
            if int(base[nr, nc]) != bg:
                return rr, cc
            rr, cc = nr, nc

    destinations: list[tuple[int, int]] = []
    for (r, c) in marker_positions:
        bc = choose_border_color_for_marker(r, c)
        dr, dc = dir_map[bc]
        destinations.append(slide_destination(r, c, dr, dc))

    out = base.copy()
    for (r, c) in destinations:
        if int(out[r, c]) != bg:
            raise ValueError(f"Marker destination {(r, c)} is not background (got {int(out[r, c])})")
        out[r, c] = MARKER

    return out.tolist()
