def solver(input_grid: list[list[int]]) -> list[list[int]]:
    """
    Inferred rule (validated on all solved training examples):
    - Let BG be the most frequent color (background).
    - Let PAINT be the rarest non-background color (a "paint marker" color).
    - Fill every connected component (4-neighborhood) of BG that does NOT touch the grid border
      with PAINT (i.e., fill all enclosed background "holes").
    - Leave all other cells unchanged.

    Notes:
    - 4-neighborhood connectivity is required (Example 4 would fail with 8-neighborhood).
    - If multiple colors tie for rarest non-background, we disambiguate using adjacency to color 1
      (a common "handle" color in this task family); otherwise we fail fast.
    """
    from collections import Counter, deque

    h = len(input_grid)
    assert h > 0, "empty grid"
    w = len(input_grid[0])
    assert w > 0, "empty rows"
    assert all(len(r) == w for r in input_grid), "non-rectangular grid"

    grid = [row[:] for row in input_grid]  # do not mutate input

    # --- Determine background color (most frequent) ---
    counts = Counter()
    for row in grid:
        counts.update(row)
    max_count = max(counts.values())
    bg_candidates = [c for c, v in counts.items() if v == max_count]
    if len(bg_candidates) == 1:
        bg = bg_candidates[0]
    else:
        # Deterministic tie-break: choose the candidate most present on the border, then smallest.
        border_presence = {}
        for c in bg_candidates:
            bp = 0
            for x in range(w):
                bp += (grid[0][x] == c)
                bp += (grid[h - 1][x] == c)
            for y in range(h):
                bp += (grid[y][0] == c)
                bp += (grid[y][w - 1] == c)
            border_presence[c] = bp
        best_bp = max(border_presence.values())
        bg_best = [c for c, bp in border_presence.items() if bp == best_bp]
        bg = min(bg_best)

    # --- Determine paint color (rarest non-background) ---
    non_bg_colors = [c for c in counts.keys() if c != bg]
    assert non_bg_colors, "grid contains only the background color"

    min_count = min(counts[c] for c in non_bg_colors)
    paint_candidates = [c for c in non_bg_colors if counts[c] == min_count]

    def any_adjacent_to_color(target_color: int, neighbor_color: int) -> bool:
        for y in range(h):
            for x in range(w):
                if grid[y][x] != target_color:
                    continue
                if y > 0 and grid[y - 1][x] == neighbor_color:
                    return True
                if y + 1 < h and grid[y + 1][x] == neighbor_color:
                    return True
                if x > 0 and grid[y][x - 1] == neighbor_color:
                    return True
                if x + 1 < w and grid[y][x + 1] == neighbor_color:
                    return True
        return False

    if len(paint_candidates) == 1:
        paint = paint_candidates[0]
    else:
        # Disambiguate ties using adjacency to color 1 when possible.
        if 1 in counts and 1 != bg:
            adj_to_1 = [c for c in paint_candidates if any_adjacent_to_color(c, 1)]
            if len(adj_to_1) == 1:
                paint = adj_to_1[0]
            else:
                raise ValueError(
                    "Ambiguous paint color: multiple least-frequent non-background colors "
                    "and tie not resolved by adjacency to color 1"
                )
        else:
            raise ValueError(
                "Ambiguous paint color: multiple least-frequent non-background colors "
                "and no color 1 available for disambiguation"
            )

    assert paint != bg, "paint color equals background (unexpected)"

    # --- Flood-fill outside background from the border (4-neighborhood) ---
    visited = [[False] * w for _ in range(h)]
    q = deque()

    def enqueue_if_bg(y: int, x: int) -> None:
        if not visited[y][x] and grid[y][x] == bg:
            visited[y][x] = True
            q.append((y, x))

    # Seed BFS with all border background cells
    for x in range(w):
        enqueue_if_bg(0, x)
        enqueue_if_bg(h - 1, x)
    for y in range(h):
        enqueue_if_bg(y, 0)
        enqueue_if_bg(y, w - 1)

    while q:
        y, x = q.popleft()
        if y > 0:
            enqueue_if_bg(y - 1, x)
        if y + 1 < h:
            enqueue_if_bg(y + 1, x)
        if x > 0:
            enqueue_if_bg(y, x - 1)
        if x + 1 < w:
            enqueue_if_bg(y, x + 1)

    # --- Fill enclosed background cells (not visited) with paint ---
    for y in range(h):
        for x in range(w):
            if grid[y][x] == bg and not visited[y][x]:
                grid[y][x] = paint

    return grid

