def solver(input_grid: list[list[int]]) -> list[list[int]]:
    """
    Inferred rule (validated on provided solved examples, with probes as coverage):
    - Grid contains exactly 3 colors: a background color (on the border) and two main colors.
    - First, denoise: any non-background pixel that is "overwhelmed" by a single neighbor color
      in its orthogonal neighborhood (>=3 of 4, or all available on edges) is replaced by that
      neighbor color. This removes stray pixels and boundary protrusions.
    - Then, find "missing pixels": background pixels that are "surrounded" by a single main color
      in their orthogonal neighborhood (>=3 of 4, or all available on edges). For each such pixel:
        * mark its center as 8
        * fill its entire 3x3 neighborhood (clipped to grid) with the opposite main color
          (the other of the two main colors), except the centers which stay 8.
    """
    from collections import Counter

    # --- Basic checks ---
    if not input_grid or not input_grid[0]:
        raise ValueError("Empty grid is not supported.")
    H = len(input_grid)
    W = len(input_grid[0])
    if any(len(row) != W for row in input_grid):
        raise ValueError("Input grid must be rectangular.")

    colors = set(v for row in input_grid for v in row)
    if 8 in colors:
        raise ValueError("Precondition failed: marker color 8 must not be present in the input.")
    if len(colors) != 3:
        raise ValueError(f"Precondition failed: expected exactly 3 colors, got {sorted(colors)}.")

    # --- Determine background as the most frequent border color ---
    border_cells = []
    for r in range(H):
        for c in range(W):
            if r == 0 or r == H - 1 or c == 0 or c == W - 1:
                border_cells.append(input_grid[r][c])
    bcnt = Counter(border_cells)
    max_b = max(bcnt.values())
    bg_candidates = [col for col, f in bcnt.items() if f == max_b]
    if len(bg_candidates) == 1:
        bg = bg_candidates[0]
    else:
        # Tie-break by overall frequency.
        total_cnt = Counter(v for row in input_grid for v in row)
        max_t = max(total_cnt[c] for c in bg_candidates)
        bg2 = [c for c in bg_candidates if total_cnt[c] == max_t]
        if len(bg2) != 1:
            raise ValueError(f"Ambiguous background color among {bg_candidates}.")
        bg = bg2[0]

    main_colors = [c for c in colors if c != bg]
    if len(main_colors) != 2:
        raise ValueError("Precondition failed: expected exactly two non-background colors.")
    a, b = main_colors
    opp = {a: b, b: a}

    def orth_neighbors(r: int, c: int):
        if r > 0:
            yield r - 1, c
        if r + 1 < H:
            yield r + 1, c
        if c > 0:
            yield r, c - 1
        if c + 1 < W:
            yield r, c + 1

    def strong_majority_color(neigh_vals: list[int]) -> tuple[int, int, int]:
        """
        Returns (best_color, best_count, threshold) where threshold=min(3, len(neigh_vals)).
        We only act when best_count >= threshold, which implies a unique best_color (no tie).
        """
        if not neigh_vals:
            return -1, 0, 0
        thr = min(3, len(neigh_vals))
        cnt = Counter(neigh_vals)
        best_color, best_count = cnt.most_common(1)[0]
        return best_color, best_count, thr

    # --- Step 1: Denoise non-background pixels (simultaneous, based on original input) ---
    clean = [row[:] for row in input_grid]
    for r in range(H):
        for c in range(W):
            v = input_grid[r][c]
            if v == bg:
                continue
            neigh = [input_grid[rr][cc] for rr, cc in orth_neighbors(r, c)]
            best_color, best_count, thr = strong_majority_color(neigh)
            if best_count >= thr and best_color != v:
                clean[r][c] = best_color

    # --- Step 2: Detect missing pixels (background cells with strong main-color surround) ---
    holes = []  # list of (r, c, surround_color)
    for r in range(H):
        for c in range(W):
            if clean[r][c] != bg:
                continue
            neigh = [clean[rr][cc] for rr, cc in orth_neighbors(r, c)]
            best_color, best_count, thr = strong_majority_color(neigh)
            if best_count >= thr and best_color != bg:
                if best_color not in opp:
                    raise ValueError("Detected a hole surrounded by an unexpected color.")
                holes.append((r, c, best_color))

    centers = {(r, c) for r, c, _ in holes}

    # --- Step 3: Apply 3x3 neighborhood fills around holes (simultaneous with conflict checks) ---
    assign = [[None for _ in range(W)] for _ in range(H)]
    for r, c, surround in holes:
        target = opp[surround]
        r0, r1 = max(0, r - 1), min(H, r + 2)
        c0, c1 = max(0, c - 1), min(W, c + 2)
        for rr in range(r0, r1):
            for cc in range(c0, c1):
                if (rr, cc) in centers:
                    # Centers will become 8; ignore potential overlaps here.
                    continue
                prev = assign[rr][cc]
                if prev is None:
                    assign[rr][cc] = target
                elif prev != target:
                    raise ValueError("Overlapping hole neighborhoods with different target colors.")

    out = [row[:] for row in clean]
    for r in range(H):
        for c in range(W):
            if assign[r][c] is not None:
                out[r][c] = assign[r][c]

    # --- Step 4: Mark hole centers as 8 ---
    for r, c, _ in holes:
        out[r][c] = 8

    return out

