def solver(input_grid: list[list[int]]) -> list[list[int]]:
    import numpy as np
    from collections import Counter, defaultdict

    # ----------------------------
    # Preconditions / parsing
    # ----------------------------
    assert isinstance(input_grid, list) and input_grid, "input_grid must be a non-empty list of rows"
    h = len(input_grid)
    w = len(input_grid[0])
    assert all(isinstance(r, list) and len(r) == w for r in input_grid), "input_grid must be rectangular"
    assert all(all(isinstance(v, int) and 0 <= v <= 9 for v in row) for row in input_grid), "values must be ints 0..9"

    grid = np.array(input_grid, dtype=int)
    uniq = set(int(v) for v in grid.ravel().tolist())

    assert 0 in uniq, "This solver expects background holes color 0 to exist"
    assert 8 not in uniq, "This solver uses color 8 as a new marker; input must not already contain 8"

    main_colors = sorted(uniq - {0})
    assert len(main_colors) == 2, f"Expected exactly 2 non-background colors besides 0, got {main_colors}"

    cA, cB = main_colors[0], main_colors[1]

    dirs4 = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    def neighbors4(r, c):
        for dr, dc in dirs4:
            nr, nc = r + dr, c + dc
            if 0 <= nr < h and 0 <= nc < w:
                yield nr, nc

    def neighbors8(r, c):
        for dr in (-1, 0, 1):
            for dc in (-1, 0, 1):
                if dr == 0 and dc == 0:
                    continue
                nr, nc = r + dr, c + dc
                if 0 <= nr < h and 0 <= nc < w:
                    yield nr, nc

    # ----------------------------
    # 1) Detect "holes" (0-cells embedded in one main color), to be marked as 8.
    #
    # Training evidence:
    # - Only certain 0-cells become 8: those surrounded mostly by a single main color.
    # - Around each such hole, a 3x3 neighborhood is painted with the opposite main color.
    #
    # Robust hole criterion (supports small clusters too):
    # - Look at 8-neighborhood counts for the two main colors.
    # - Dominant color must appear at least min(5, degree8) times.
    # - The other main color must appear at most 1 time (allows a single diagonal boundary contact).
    # - Also require at least 2 occurrences of the dominant color in the 4-neighborhood.
    # ----------------------------
    holes = {}  # (r,c) -> region_color (dominant color around the 0)
    for r in range(h):
        for c in range(w):
            if grid[r, c] != 0:
                continue

            cntA = cntB = 0
            deg8 = 0
            for nr, nc in neighbors8(r, c):
                deg8 += 1
                v = grid[nr, nc]
                if v == cA:
                    cntA += 1
                elif v == cB:
                    cntB += 1

            if deg8 == 0:
                continue  # 1x1 grid corner case

            # Pick dominant main color in 8-neighborhood
            if cntA >= cntB:
                dom, dom_cnt, oth_cnt = cA, cntA, cntB
            else:
                dom, dom_cnt, oth_cnt = cB, cntB, cntA

            thresh8 = min(5, deg8)
            if dom_cnt < thresh8:
                continue
            if oth_cnt > 1:
                continue

            dom4 = 0
            deg4 = 0
            for nr, nc in neighbors4(r, c):
                deg4 += 1
                if grid[nr, nc] == dom:
                    dom4 += 1
            if dom4 < min(2, deg4):
                continue

            holes[(r, c)] = dom

    # ----------------------------
    # 2) Denoise / prune spurious pixels of the main colors.
    #
    # Observed in training:
    # - Stray single pixels and 1-cell "spikes" of a main color (even if connected) are removed,
    #   by replacing them with the most common value among 4-neighbors (often 0 or the other main color).
    #
    # Implemented as monotone pruning per color:
    # - For each main color, repeatedly remove pixels that have <=1 same-color 4-neighbors.
    # - Process the "more bottom-right" color first (by max(row+col)), to avoid symmetric oscillations.
    # ----------------------------
    work = grid.copy()

    def bottom_right_score(color: int) -> int:
        coords = np.argwhere(work == color)
        assert coords.size > 0, f"Main color {color} not present in grid"
        return int(np.max(coords[:, 0] + coords[:, 1]))

    prune_order = sorted(main_colors, key=bottom_right_score, reverse=True)

    def choose_replacement(color: int, neigh_vals: list[int]) -> int:
        # Mode with deterministic tie-break:
        # - Prefer background 0 if tied.
        # - Otherwise prefer any non-'color' candidate (so we actually prune endpoints).
        # - Otherwise smallest.
        ctr = Counter(neigh_vals)
        maxcnt = max(ctr.values())
        candidates = [v for v, cnt in ctr.items() if cnt == maxcnt]
        if len(candidates) == 1:
            return candidates[0]
        if 0 in candidates:
            return 0
        non_self = [v for v in candidates if v != color]
        if non_self:
            return min(non_self)
        return min(candidates)

    max_total_prune_iters = h * w + 5  # strict upper bound for safety
    for color in prune_order:
        # Because we only ever change 'color' pixels away from 'color', the count is monotone decreasing.
        for _ in range(max_total_prune_iters):
            to_change = []
            for r in range(h):
                for c in range(w):
                    if work[r, c] != color:
                        continue
                    same = 0
                    neigh = []
                    for nr, nc in neighbors4(r, c):
                        v = int(work[nr, nc])
                        neigh.append(v)
                        if v == color:
                            same += 1
                    if same <= 1:
                        repl = choose_replacement(color, neigh)
                        if repl != color:
                            to_change.append((r, c, repl))
            if not to_change:
                break
            for r, c, repl in to_change:
                work[r, c] = repl
        else:
            raise ValueError("Pruning did not converge (unexpected for monotone pruning)")

    # Ensure both main colors still exist after denoising (they are supposed to be large regions)
    for color in main_colors:
        assert np.any(work == color), f"After denoising, main color {color} disappeared; input violates learned structure"

    # ----------------------------
    # 3) Apply halos around each hole:
    # - For hole in region color X, paint its 3x3 neighborhood with the other main color Y.
    # - Mark the hole itself as 8.
    # Apply simultaneously; if conflicting halos overlap, fail-fast.
    # ----------------------------
    def other_color(x: int) -> int:
        return cB if x == cA else cA

    influence = defaultdict(set)  # (r,c) -> {target_color}
    for (r, c), region_col in holes.items():
        target = other_color(region_col)
        for dr in (-1, 0, 1):
            for dc in (-1, 0, 1):
                nr, nc = r + dr, c + dc
                if 0 <= nr < h and 0 <= nc < w:
                    influence[(nr, nc)].add(target)

    for pos, targets in influence.items():
        if len(targets) > 1:
            raise ValueError("Overlapping hole halos request conflicting target colors; ambiguous under learned rule")

    out = work.copy()
    for (r, c), targets in influence.items():
        out[r, c] = next(iter(targets))

    for (r, c) in holes.keys():
        out[r, c] = 8

    return out.tolist()
