def solver(input):
    grid = [list(row) for row in input]
    H, W = len(grid), len(grid[0])

    from collections import Counter

    # Find background color (most common)
    all_colors = [grid[i][j] for i in range(H) for j in range(W)]
    bg = Counter(all_colors).most_common(1)[0][0]

    # Find divider color (forms longest lines)
    def get_line_length(i, j, color):
        h_len, v_len = 1, 1
        for dj in [-1, 1]:
            jj = j + dj
            while 0 <= jj < W and grid[i][jj] == color: h_len += 1; jj += dj
        for di in [-1, 1]:
            ii = i + di
            while 0 <= ii < H and grid[ii][j] == color: v_len += 1; ii += di
        return max(h_len, v_len)

    color_max_line = {}
    for i in range(H):
        for j in range(W):
            if grid[i][j] != bg:
                color_max_line[grid[i][j]] = max(color_max_line.get(grid[i][j], 0), get_line_length(i, j, grid[i][j]))

    divider_color = max(color_max_line, key=lambda c: color_max_line[c]) if color_max_line else None
    is_divider = [[grid[i][j] == divider_color for j in range(W)] for i in range(H)]

    visited = [[False]*W for _ in range(H)]
    output = [row[:] for row in grid]

    def flood_fill(si, sj):
        cells, stack = [], [(si, sj)]
        visited[si][sj] = True
        while stack:
            i, j = stack.pop()
            cells.append((i, j))
            for di, dj in [(-1,0), (1,0), (0,-1), (0,1)]:
                ni, nj = i + di, j + dj
                if 0 <= ni < H and 0 <= nj < W and not visited[ni][nj] and not is_divider[ni][nj]:
                    visited[ni][nj] = True
                    stack.append((ni, nj))
        return cells

    def get_diagonal_colors(bbox):
        min_i, max_i, min_j, max_j = bbox
        corners = [(min_i, min_j, 1, 1), (min_i, max_j, 1, -1), (max_i, min_j, -1, 1), (max_i, max_j, -1, -1)]
        best_colors, best_count = None, -1

        for ci, cj, di, dj in corners:
            colors, i, j = [], ci, cj
            while min_i <= i <= max_i and min_j <= j <= max_j:
                if not is_divider[i][j]: colors.append(grid[i][j])
                else: break
                i, j = i + di, j + dj
            non_bg = sum(1 for c in colors if c != bg)
            if non_bg > best_count: best_count, best_colors = non_bg, colors

        if not best_colors: return [bg]
        unique = []
        for c in best_colors:
            if c not in unique: unique.append(c)
            if len(unique) >= 3: break
        while len(unique) > 1 and unique[-1] == bg: unique.pop()
        return unique if unique else [bg]

    for i in range(H):
        for j in range(W):
            if not visited[i][j] and not is_divider[i][j]:
                cells = flood_fill(i, j)
                min_i, max_i = min(c[0] for c in cells), max(c[0] for c in cells)
                min_j, max_j = min(c[1] for c in cells), max(c[1] for c in cells)
                colors = get_diagonal_colors((min_i, max_i, min_j, max_j))
                for ci, cj in cells:
                    layer = min(ci - min_i, max_i - ci, cj - min_j, max_j - cj)
                    output[ci][cj] = colors[layer % len(colors)]

    return output
