            counts[v] = counts.get(v, 0) + 1
    bg = max(counts, key=counts.get)

    # Collect info for each non-background color
    info = {}  # color -> [count, min_r, min_c]
    for r in range(h):
        for c in range(w):
            v = grid[r][c]
            if v == bg:
                continue
            if v not in info:
                info[v] = [0, r, c]
            info[v][0] += 1
            if r < info[v][1] or (r == info[v][1] and c < info[v][2]):
                info[v][1], info[v][2] = r, c

    # Sort colors by their first appearance (top-to-bottom, then left-to-right)
    colors_sorted = sorted(info.items(), key=lambda kv: (kv[1][1], kv[1][2]))

    # Build single-column output: each color repeated by its pixel count
    out = []
    for color, (cnt, _, _) in colors_sorted:
        out.extend([[color]] * cnt)

    return out
